import React from 'react';
import { Chart } from 'react-chartjs-2';
import 'chart.js/auto';

function GanttChartComponent({ schedule }) {
  const data = {
    datasets: schedule.map((task, index) => ({
      label: `Job ${task.job_id}`,
      data: [{
        x: [task.start_time, task.end_time],
        y: task.machine_id,
      }],
      backgroundColor: `hsl(${index * 60}, 70%, 50%)`,
    })),
  };

  const options = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'minute',
        },
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Machine',
        },
      },
    },
    plugins: {
      legend: {
        display: true,
      },
    },
  };

  return (
    <div className="gantt-chart">
      <h2>Gantt Chart</h2>
      <Chart type="bar" data={data} options={options} />
    </div>
  );
}

export default GanttChartComponent;
